INSERT INTO `wp_bcyw_posts` VALUES('37', '2', '2015-03-08 01:47:36', '2015-03-08 01:47:36', 'The non-profit PACE Financing Authority of Texas is the official administrator of Property Assessed Clean Energy (PACE) financing programs in Texas. PACE is an innovative financing program that enables owners of commercial and industrial properties to obtain low-cost, long-term loans for water conservation, energy-efficiency improvements, and renewable retrofits.', 'Home', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2015-03-08 01:47:36', '2015-03-08 01:47:36', '', '4', 'http://www.texaspaceauthority.org/?p=37', '0', 'revision', '', '0');
