INSERT INTO `wp_bcyw_posts` VALUES('78', '2', '2015-03-16 16:54:01', '2015-03-16 16:54:01', 'The non-profit Texas PACE Authority is the official administrator of the first Property Assessed Clean Energy (PACE) financing program in Texas. PACE is an innovative financing program that enables owners of commercial and industrial properties to obtain low-cost, long-term loans for water conservation, energy-efficiency improvements, and renewable retrofits.\r\n\r\nClick <a href=\"https://www.youtube.com/watch?v=R3Ota3uvM1E\">here</a> for a short video explaining PACE:\r\n\r\n<a href=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/03/Capture-e1427300183845.jpg\"><img class=\"alignnone size-medium wp-image-179\" src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/03/Capture-e1427300183845-300x200.jpg\" alt=\"PACE Infographic\" width=\"300\" height=\"200\" /></a>\r\n\r\n&nbsp;', 'Property Assessed Clean Energy', '', 'publish', 'closed', 'closed', '', 'property-assessed-clean-energy', '', '', '2015-05-01 05:59:34', '2015-05-01 05:59:34', '', '0', 'http://www.texaspaceauthority.org/?p=78', '0', 'post', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('79', '2', '2015-03-16 16:54:01', '2015-03-16 16:54:01', 'The non-profit PACE Financing Authority of Texas is the official administrator of Property Assessed Clean Energy (PACE) financing programs in Texas. PACE is an innovative financing program that enables owners of commercial and industrial properties to obtain low-cost, long-term loans for water conservation, energy-efficiency improvements, and renewable retrofits.', 'Property Assessed Clean Energy', '', 'inherit', 'closed', 'closed', '', '78-revision-v1', '', '', '2015-03-16 16:54:01', '2015-03-16 16:54:01', '', '78', 'http://www.texaspaceauthority.org/?p=79', '0', 'revision', '', '0');
