INSERT INTO `wp_bcyw_posts` VALUES('492', '1', '2015-06-18 18:25:48', '2015-06-18 18:25:48', '&nbsp;\r\nTexas PACE Authority does not endorse any particular PACE lenders. The list below is provided for convenience if you need help in finding a lender who meets the PACE in a Box recommendations and is interested in financing PACE projects. \r\n\r\n<strong>LENDERS</strong>\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/CleanFund_LOGO.jpg\" alt=\"Clean Fund LLC\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Clean Fund LLC</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $500K - $15M\r\n<strong>Types of Projects:</strong> Any\r\n<strong>Contact:</strong> <a href=\"mailto:joshua.kagan@cleanfund.com\">joshua.kagan@cleanfund.com</a>\r\n<a href=\"http://www.cleanfund.com\" target=\"_blank\">cleanfund.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/Greenworks-Lending-Logo.jpg\" alt=\"Greenworks Lending\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Greenworks Lending</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $30K - $5M\r\n<strong>Types of Projects:</strong> Any Eligible Technologies and Properties\r\n<strong>Contact:</strong> <a href=\"mailto:azech@greenworkslending.com\">azech@greenworkslending.com</a>\r\n<a href=\"http://www.greenworkslending.com\" target=\"_blank\">greenworkslending.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/PACEEquity-ALT.-Tag-Vert.jpg\" alt=\"PACE Equity Finance\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>PACE Equity Finance</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $250K - $20M\r\n<strong>Types of Projects:</strong> Any\r\n<strong>Contact:</strong> <a href=\"mailto:kmoyer@pace-equity.com\">kmoyer@pace-equity.com</a>\r\n<a href=\"http://www.pace-equity.com\" target=\"_blank\">pace-equity.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168px\"><img src=\"http://www.petros-pace.com/wp-content/themes/PACE/images/logo.png\" alt=\"PETROS PACE Finance\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>PETROS PACE Finance</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $250K+\r\n<strong>Types of Projects:</strong> All Qualified Projects\r\n<strong>Contact:</strong> <a href=\"mailto:mansoor@petrospartners.com\">mansoor@petrospartners.com</a>\r\n<a href=\"http://www.petros-pace.com\" target=\"_blank\">PETROS-PACE.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/SFS-Logo-1.jpg\" alt=\"Seminole Financial Services, LLC\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Seminole Financial Services, LLC</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $2M - $40M\r\n<strong>Types of Projects:</strong> Solar, Wind\r\n<strong>Contact:</strong> <a href=\"mailto:cdiaz@seminolefniancialservices.com\">cdiaz@seminolefniancialservices.com</a>\r\n<a href=\"http://www.seminolefinancialservices.com\" target=\"_blank\">seminolefinancialservices.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/sfa.png\" alt=\"Structured Finance Associates\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Structured Finance Associates</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $500K+\r\n<strong>Types of Projects:</strong> Apartments, hotels, retail, offices, industrial\r\n<strong>Contact:</strong> <a href=\"mailto:ljdunn@strucfinance.com\">ljdunn@strucfinance.com</a>\r\n<a href=\"http://www.strucfinance.com\" target=\"_blank\">strucfinance.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/360PL_websitelogo.png\" alt=\"360 PACE Lending\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>360 PACE Lending</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $500K+\r\n<strong>Types of Projects:</strong> Any\r\n<strong>Contact:</strong> <a href=\"mailto:moore@360PaceLending.com\">moore@360PaceLending.com</a>\r\n<a href=\"http://www.360PaceLending.com\" target=\"_blank\">360PaceLending.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n', 'Lender List', '', 'inherit', 'closed', 'closed', '', '338-revision-v1', '', '', '2015-06-18 18:25:48', '2015-06-18 18:25:48', '', '338', 'http://www.texaspaceauthority.org/338-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('493', '1', '2015-06-18 20:30:30', '2015-06-18 20:30:30', '&nbsp;\r\nTexas PACE Authority does not endorse any particular PACE lenders. The list below is provided for convenience if you need help in finding a lender who meets the PACE in a Box recommendations and is interested in financing PACE projects. \r\n\r\n<strong>LENDERS</strong>\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/CleanFund_LOGO.jpg\" alt=\"Clean Fund LLC\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Clean Fund LLC</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $500K - $15M\r\n<strong>Types of Projects:</strong> Any\r\n<strong>Contact:</strong> <a href=\"mailto:joshua.kagan@cleanfund.com\">joshua.kagan@cleanfund.com</a>\r\n<a href=\"http://www.cleanfund.com\" target=\"_blank\">cleanfund.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/Greenworks-Lending-Logo.jpg\" alt=\"Greenworks Lending\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Greenworks Lending</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $30K - $5M\r\n<strong>Types of Projects:</strong> Any Eligible Technologies and Properties\r\n<strong>Contact:</strong> <a href=\"mailto:azech@greenworkslending.com\">azech@greenworkslending.com</a>\r\n<a href=\"http://www.greenworkslending.com\" target=\"_blank\">greenworkslending.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/PACEEquity-ALT.-Tag-Vert.jpg\" alt=\"PACE Equity Finance\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>PACE Equity Finance</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $250K - $20M\r\n<strong>Types of Projects:</strong> Any\r\n<strong>Contact:</strong> <a href=\"mailto:demmerich@pace-equity.com\">demmerich@pace-equity.com</a>\r\n<a href=\"http://www.pace-equity.com\" target=\"_blank\">pace-equity.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168px\"><img src=\"http://www.petros-pace.com/wp-content/themes/PACE/images/logo.png\" alt=\"PETROS PACE Finance\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>PETROS PACE Finance</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $250K+\r\n<strong>Types of Projects:</strong> All Qualified Projects\r\n<strong>Contact:</strong> <a href=\"mailto:mansoor@petrospartners.com\">mansoor@petrospartners.com</a>\r\n<a href=\"http://www.petros-pace.com\" target=\"_blank\">PETROS-PACE.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/SFS-Logo-1.jpg\" alt=\"Seminole Financial Services, LLC\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Seminole Financial Services, LLC</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $2M - $40M\r\n<strong>Types of Projects:</strong> Solar, Wind\r\n<strong>Contact:</strong> <a href=\"mailto:cdiaz@seminolefniancialservices.com\">cdiaz@seminolefniancialservices.com</a>\r\n<a href=\"http://www.seminolefinancialservices.com\" target=\"_blank\">seminolefinancialservices.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/sfa.png\" alt=\"Structured Finance Associates\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>Structured Finance Associates</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $500K+\r\n<strong>Types of Projects:</strong> Apartments, hotels, retail, offices, industrial\r\n<strong>Contact:</strong> <a href=\"mailto:ljdunn@strucfinance.com\">ljdunn@strucfinance.com</a>\r\n<a href=\"http://www.strucfinance.com\" target=\"_blank\">strucfinance.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n<hr />\r\n\r\n<table>\r\n<tr>\r\n<td class=\"lender-logo\" width=\"200\" height=\"168x\"><img src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/05/360PL_websitelogo.png\" alt=\"360 PACE Lending\" width=\"200\" />\r\n</td>\r\n\r\n<td width=\"15px\"></td>\r\n\r\n<td width=\"340px\">\r\n<strong>360 PACE Lending</strong>\r\n<span style=\"font-size: small;\"><strong>Preferred Financing Range:</strong> $500K+\r\n<strong>Types of Projects:</strong> Any\r\n<strong>Contact:</strong> <a href=\"mailto:moore@360PaceLending.com\">moore@360PaceLending.com</a>\r\n<a href=\"http://www.360PaceLending.com\" target=\"_blank\">360PaceLending.com</a>\r\n</span>\r\n</td>\r\n</tr>\r\n</table>\r\n\r\n', 'Lender List', '', 'inherit', 'closed', 'closed', '', '338-revision-v1', '', '', '2015-06-18 20:30:30', '2015-06-18 20:30:30', '', '338', 'http://www.texaspaceauthority.org/338-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('496', '1', '2015-06-26 20:19:02', '2015-06-26 20:19:02', '<h2 class=\"font_8\">Find a Lender</h2>\r\n<p class=\"font_8\">The Texas PACE Authority operates via an open-market model; consequently, you are not excluded from finding your own source of financing. For convenience, we have consolidated a list of lenders which we know to be active and familiar with PACE, and which meet the standards recommended by PACE in a Box.\r\n\r\n[su_button url=\"http://www.texaspaceauthority.org/?page_id=338 \" target=\"self\" style=\"flat\" background=\"#29789A\" size=\"15\" center=\"yes\" radius=\"0\" icon=\"icon: search\"]Find a Lender[/su_button]\r\n\r\n<BR>\r\n\r\n<h2 class=\"font_8\">Find a Contractor</h2>\r\n<p class=\"font_8\">Note that the Texas PACE Authority neither recommends nor endorses any particular PACE Service providers. This directory is provided solely for convenience in consolidating a list of PACE service providers. If you would like to be included in the Texas Green Building Marketplace, please contact the Texas Chapters of the USGBC at 512-470-9923 or info@texasgreenbuildingmarketplace.org.\r\n\r\n<a href=\"http://texasgreenbuildingmarketplace.org/listing/guide/pace-related\"><img class=\" size-medium wp-image-171 aligncenter\" src=\"http://www.texaspaceauthority.org/wp-content/uploads/2015/03/Texas_GreenBuilding_Marketplace_440-300x169.png\" alt=\"\" width=\"300\" height=\"169\" /></a>\r\n\r\n', 'Service Providers', '', 'inherit', 'closed', 'closed', '', '103-revision-v1', '', '', '2015-06-26 20:19:02', '2015-06-26 20:19:02', '', '103', 'http://www.texaspaceauthority.org/103-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('497', '1', '2015-06-26 20:43:40', '2015-06-26 20:43:40', 'Travis County recently passed the first PACE (Property Assessed Clean Energy) program in the state, enabling property owners to improve the energy and water efficiency of their buildings, and pay for the upgrades via a property tax assessment. \r\n\r\nOn June 24th, Commissioner Daugherty and Commissioner Shea hosted a briefing on PACE and how property owners can access this new financing tool in Travis County. In case you missed it, check out the briefing below.\r\n\r\nPanelists include:\r\nJonathon Blackburn (Moderator) - Texas PACE Authority\r\nKristene Canady - Simon Properties\r\nMansoor Ghori - Petros Partners\r\nDenise Kuehn - Austin Energy\r\nSteve Minick - Texas Association of Business\r\n\r\n<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/vzN5dD-bu64\" frameborder=\"0\" allowfullscreen></iframe>\r\n\r\nPhoto Credit: \"<a href=\"https://www.flickr.com/photos/adam10414/18766022969/in/photolist-uAhLQa-9TzVXy-rDP8oX-rMZ3DH-7knxQL-K25SR-6A4YzC-avzUrM-9e9aTi-P56aw-P55kq-K2dVg-CUgop-v3ujW-4xgetM-4xkFtv-4dXxaq-3Nb8Lh-fNjXm-39wC4B-bB5ngp-8ELFNx-kD5vqW-LKxTJ-8AfcfQ-7KYfdJ-5XSen7-e3iwpL-dXQqkZ-68uv4P-68un4V-68uMCr-68yYbu-68yS6b-68yQv5-6RpKrr-68yMCm-6RtFEN-6RtS57-6RtBRQ-aftvWf-e3X4Hv-dvEuj2-sjpqCj-kY4w4g-dDGt4T-9MDt7M-cMigHQ-75h5qJ-rDQwgt\">Red Sunset @ Sail & Ski</a>\" by <a href=\"https://www.flickr.com/photos/adam10414/\">Adam D</a> is licensed under <a href=\"https://creativecommons.org/licenses/by/2.0/\">CC BY 2.0</a>', 'Travis County PACE Luncheon', '', 'publish', 'closed', 'closed', '', 'travis-county-pace-luncheon', '', '', '2015-06-26 20:53:16', '2015-06-26 20:53:16', '', '0', 'http://www.texaspaceauthority.org/?p=497', '0', 'post', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('498', '1', '2015-06-26 20:22:07', '2015-06-26 20:22:07', 'Travis County recently passed the first PACE (Property Assessed Clean Energy) program in the state, enabling property owners to improve the energy and water efficiency of their buildings, and pay for the upgrades via a property tax assessment. \r\n\r\nOn June 24th, Commissioner Daugherty and Commissioner Shea hosted a briefing on PACE and how property owners can access this new financing tool in Travis County. \r\n\r\n<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/vzN5dD-bu64\" frameborder=\"0\" allowfullscreen></iframe>', 'Travis County PACE Luncheon', '', 'inherit', 'closed', 'closed', '', '497-revision-v1', '', '', '2015-06-26 20:22:07', '2015-06-26 20:22:07', '', '497', 'http://www.texaspaceauthority.org/497-revision-v1/', '0', 'revision', '', '0');
