INSERT INTO `wp_bcyw_posts` VALUES('681', '1', '2015-10-20 18:11:00', '2015-10-20 18:11:00', 'The Travis County Page\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n$({countNum: 99}).animate({countNum: 1000}, {\r\n  duration: 8000,\r\n  easing:\'linear\',\r\n  step: function() {\r\n    // What todo on every count\r\n    console.log(Math.floor(this.countNum));\r\n  },\r\n  complete: function() {\r\n    console.log(\'finished\');\r\n  }\r\n});', 'Travis County PACE', '', 'inherit', 'closed', 'closed', '', '677-revision-v1', '', '', '2015-10-20 18:11:00', '2015-10-20 18:11:00', '', '677', 'http://www.texaspaceauthority.org/677-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('680', '1', '2015-10-20 18:09:37', '2015-10-20 18:09:37', 'The Travis County Page\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<script type=\"text/javascript\"><!--\r\n    jQuery(function($) {\r\n        $(\'.timer\').countTo({\r\n            from: 50,\r\n            to: 2500,\r\n            speed: 1000,\r\n            refreshInterval: 50,\r\n            onComplete: function(value) {\r\n                console.debug(this);\r\n            }\r\n        });\r\n    });\r\n//--></script>\r\n\r\n<span class=\"timer\"></span>', 'Travis County PACE', '', 'inherit', 'closed', 'closed', '', '677-revision-v1', '', '', '2015-10-20 18:09:37', '2015-10-20 18:09:37', '', '677', 'http://www.texaspaceauthority.org/677-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('683', '1', '2015-10-20 18:20:14', '2015-10-20 18:20:14', 'The Travis County Page\r\n\r\n\r\n', 'Travis County PACE', '', 'inherit', 'closed', 'closed', '', '677-revision-v1', '', '', '2015-10-20 18:20:14', '2015-10-20 18:20:14', '', '677', 'http://www.texaspaceauthority.org/677-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('682', '1', '2015-10-20 18:14:55', '2015-10-20 18:14:55', 'The Travis County Page\r\n\r\n\r\n<!doctype html>\r\n<html>\r\n    <head>\r\n        <title>Counter Animation using JavaScript</title>\r\n        <script type=\"text/javascript\" src=\"https://cdn.rawgit.com/inorganik/countUp.js/master/countUp.min.js\"></script>\r\n    </head>\r\n    <body>\r\n        <h1><span id=\"number\">249</span> shares</h1>\r\n\r\n        <script type=\"text/javascript\">\r\n            var numAnim = new countUp(document.getElementById(\"number\"), 0, 249);\r\n            numAnim.start();\r\n        </script>\r\n    </body>\r\n</html>', 'Travis County PACE', '', 'inherit', 'closed', 'closed', '', '677-revision-v1', '', '', '2015-10-20 18:14:55', '2015-10-20 18:14:55', '', '677', 'http://www.texaspaceauthority.org/677-revision-v1/', '0', 'revision', '', '0');
INSERT INTO `wp_bcyw_posts` VALUES('685', '1', '2015-10-22 19:46:29', '2015-10-22 19:46:29', 'Recently, The Rockefeller Brothers Fund pledged to match $25,000 in funds raised to support the expansion and administration of PACE in a Box programs. Invest in the Texas PACE Authority  and see your contribution double. Please support out matching campaign. Our goal is to be the statewide administrator of PACE programs, and to do so in a lean, not-for-profit manner. Help accelerate our expansion by supporting us today.\r\n\r\nDonate now at:\r\n\r\n[su_button url=\"http://pacethebusinessofabundance.causevox.com/\" target=\"self\" style=\"flat\" background=\"#29789A\" size=\"15\" center=\"yes\" radius=\"0\" icon=\"icon: search\"]Support our Cause[/su_button]\r\n\r\n<br>\r\n\r\nPhoto Credit: \"<a href=\"https://www.flickr.com/photos/ravedelay/13905785056/in/photolist-nbNMqL-6f2Ajw-niXNrQ-bqDzXY-bqDzYE-6fiP2Y-9yJgXi-6cR4ss-9yyjfW-GKsSL-bG1qkM-GKsPX-GKqP5-GKuvn-GKvAa-GKtCu-2txckZ\">Bluebonnet Sunrise</a>\" by <a href=\"https://www.flickr.com/photos/ravedelay/\">Kelly DeLay</a> is licensed under <a href=\"https://creativecommons.org/licenses/by/2.0/\">CC BY 2.0</a>', 'Help Bring PACE to All of Texas!', '', 'publish', 'closed', 'closed', '', 'help-bring-pace-to-all-of-texas', '', '', '2015-10-22 19:46:29', '2015-10-22 19:46:29', '', '0', 'http://www.texaspaceauthority.org/?p=685', '0', 'post', '', '0');
