INSERT INTO `wp_bcyw_posts` VALUES('759', '2', '2015-10-28 15:43:51', '2015-10-28 15:43:51', '/*\nWelcome to Custom CSS!\n\nCSS (Cascading Style Sheets) is a kind of code that tells the browser how\nto render a web page. You may delete these comments and get started with\nyour customizations.\n\nBy default, your stylesheet will be loaded after the theme stylesheets,\nwhich means that your rules can take precedence and override the theme CSS\nrules. Just write here what you want to change, you don\'t need to copy all\nyour theme\'s stylesheet content.\n*/\nh1,h2,h3,h4,h5,span,a.entry-header {\n	font-family: font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif;\n}\n\np,ol,ul,li,.entry-content,label,input, .gform-description {\n	font-family: font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif;\n}\n\n#secondary .widget {\n	border: 0;\n	box-shadow: 0 0 0;\n	background-color: #FFFFFF;\n	margin-bottom: 0;\n	margin-top: 0;\n	padding-top: 10px;\n	padding-bottom: 0;\n}\n\na {\n	font-weight: normal;\n}\n\n.sidebar {\n	display: inline-block;\n	background-color: #29789A;\n	text-align: center;\n	padding: 30px;\n	color: white;\n	font-size: 24px;\n	height: 125px;\n}\n\ntd.lender-logo {\n	vertical-align: middle;\n	width: 150px;\n}\n\n.entry-meta {\n	display: none;\n}\n\nfooter.widget-area {\n	margin-bottom: 0;\n}\n\ndiv#footer-sidebar.container {\n	background-color: #F5F5F5;\n}\n\ndiv#first.widget-area, \ndiv#second.widget-area,\ndiv#third.widget-area,\ndiv#fourth.widget-area,\ndiv#first.widget-area a, \ndiv#second.widget-area a,\ndiv#third.widget-area a,\ndiv#fourth.widget-area a {\n	color: #757575;\n}\n\n.powered {\n	display: hidden;\n}\n\n.site {\n	margin-top: 0;\n	margin-bottom: 0;\n}\n\n.header-sidebar {\n	padding: 0 10px 10px 0;\n}\n\nh1.entry-title {\n	line-height: 1.3;\n}\n\ndiv#site-logo {\n	padding-top: 20px;\n	padding-bottom: 10px;\n}\n\ndiv#menu-header-container {\n	padding-bottom: 10px;\n}\n\ndiv#hgroup-wrap.container {\n	padding-bottom: 20px;\n}\n\ndiv#main.container {\n	padding-left: 20px;\n	padding-left: 20px;\n	padding-top: 20px;\n	padding-bottom: 20px;\n}\n\ndiv#header-right.header-sidebar.widget-area {\n	padding-top: 35px;\n}\n\nbody,\nbutton,\ninput,\nselect,\ntextarea {\n	font-size: 12px;\n	font-size: 1.6rem;\n}\n\ndiv#header-menu, #content, .entry-content, label {\n	font-size: 14px;\n	font-weight: normal;\n}\n\nh1.entry-title, header.entry-header, div.right-section {\n	font-size: 14px;\n	font-weight: bold;\n}\n\n.header-sidebar .widget ul.menu a {\n	padding: 0 10px;\n}\n\n#main #featured-post, .hentry {\n	margin-bottom: 20px;\n	padding-bottom: 0;\n}\n\n.mc4wp-form button, .mc4wp-form input[type=submit] {\n	background-color: #26799C !important;\n}\n\n.spoiler-helper {\n	margin-bottom: 1.5em;\n}\n\n.fa {\n	background-color: #26799C;\n}', 'safecss', 'Catch Kathmandu Pro', 'inherit', 'closed', 'closed', '', '70-revision-v1', '', '', '2015-10-28 15:43:51', '2015-10-28 15:43:51', '', '70', 'http://www.texaspaceauthority.org/70-revision-v1/', '0', 'revision', '', '0');
