INSERT INTO `wp_bcyw_posts` VALUES('778', '1', '2015-10-31 14:05:34', '2015-10-31 14:05:34', 'On November 4th, the City of Houston became the second area in Texas to implement a PACE district. Property owners in the City are now able to take advantage of PACE financing - upgrading their buildings and in the process increasing their net operating income. \r\n\r\nThanks to the Houston City Council and City staff for working diligently to bring the program. A special thanks to the Mayor and her staff in spearheading this initiative, one which will benefit Houston businesses  and the environment at the same time. \r\n\r\nWelcome, Houston!\r\n\r\n<br>\r\n\r\nPhoto Credit: \"<a href=\"https://www.flickr.com/photos/76771463@N02/8311996895/in/photolist-dEva6i-DAsyj-eRUoYS-2Me8H-sbELTq-rUf76Y-hF8WiZ-fq3tor-ekaciC-eHCfQs-dv9ykC-n3ckCZ-26Jy6L-2zhW9A-9sg8TM-dAWLsR-dt2chK-dCZ4nc-akA8iG-81i9YG-9toYqK-cPMG7h-bf4M8a-biaY9e-bwpH4v-nZkzZX-n4M7Tt-kmRn24-9FtQ6c-dw94pm-cFSHAs-8U6ja9-oZjnQ6-fqAojx-nL2N9W-aSAF2Z-7etUjB-pWUh8h-4WKxdz-bvw4y2-6NtB-8d82EL-7NVPTp-eeB6qR-aSAF1V-9adQas-26WxtD-bsPsTJ-668iK9-bmQeL\">Houston</a>\" by <a href=\"https://www.flickr.com/photos/76771463@N02/\">Katie Haugland</a> is licensed under <a href=\"https://creativecommons.org/licenses/by/2.0/\">CC BY 2.0</a>', 'Welcome, Houston!', '', 'inherit', 'closed', 'closed', '', '776-revision-v1', '', '', '2015-10-31 14:05:34', '2015-10-31 14:05:34', '', '776', 'http://www.texaspaceauthority.org/776-revision-v1/', '0', 'revision', '', '0');
