iv#main.container {\n	padding-left: 20px;\n	padding-left: 20px;\n	padding-top: 20px;\n	padding-bottom: 20px;\n}\n\ndiv#header-right.header-sidebar.widget-area {\n	padding-top: 35px;\n}\n\nbody,\nbutton,\ninput,\nselect,\ntextarea {\n	font-size: 12px;\n	font-size: 1.6rem;\n}\n\ndiv#header-menu, #content, .entry-content, label {\n	font-size: 14px;\n	font-weight: normal;\n}\n\nh1.entry-title, header.entry-header, div.right-section {\n	font-size: 14px;\n	font-weight: bold;\n}\n\n.header-sidebar .widget ul.menu a {\n	padding: 0 10px;\n}\n\n#main #featured-post, .hentry {\n	margin-bottom: 20px;\n	padding-bottom: 0;\n}\n\n.mc4wp-form button, .mc4wp-form input[type=submit] {\n	background-color: #26799C !important;\n}\n\n.spoiler-helper {\n	margin-bottom: 1.5em;\n}\n\n.fa {\n	background-color: #26799C;\n}\n\n.tight-bottom, .su-row {\n	padding-bottom: 0;\n}', 'safecss', 'Catch Kathmandu Pro', 'inherit', 'closed', 'closed', '', '70-revision-v1', '', '', '2015-11-12 16:40:38', '2015-11-12 16:40:38', '', '70', 'http://www.texaspaceauthority.org/70-revision-v1/', '0', 'revision', '', '0');
