INSERT INTO `wp_bcyw_posts` VALUES('971', '6', '2016-03-31 13:19:02', '2016-03-31 13:19:02', '<strong>Events and Training</strong>\r\n\r\n<hr />\r\n\r\nOur signature events are a series of contractor training workshops. These half-day workshops will answer all your in-depth questions about PACE. Sign up for our newsletter to be notified of trainings as they are scheduled.\r\n\r\n<strong>Speaking Events</strong>\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n\r\n<a href=\"http://www.nfmt.com/highperformance/\">NFMT High Performance Building Conference </a>\r\n\r\n&nbsp;\r\n\r\n<img class=\"alignnone size-medium wp-image-968\" src=\"http://www.texaspaceauthority.org/wp-content/uploads/HPB2016_outline-204x300.jpg\" alt=\"HPB2016_outline\" width=\"204\" height=\"300\" />\r\n\r\n<a href=\"https://www.cxenergy.com/\">CxEnergy 2016</a>\r\n\r\n<img class=\"alignnone  wp-image-970\" src=\"http://www.texaspaceauthority.org/wp-content/uploads/Cx_2016-Logo-FINAL-257x300.jpg\" alt=\"Cx_2016 Logo FINAL\" width=\"206\" height=\"240\" />\r\n\r\n&nbsp;\r\n\r\nFor questions, contact admin@texaspaceauthority.org\r\n\r\nPhoto Credit: \"<a href=\"https://www.flickr.com/photos/usacehq/5056771149/in/photolist-8GRgTt-9GzLgu-9A9WT9-9HEdHK-ayZJhh-9VTAzN-apamtg-7VdRWo-9XJcec-9Gx3AT-ewVayZ-aoWWxA-ayX5D6-m8rF6Y-bXPwWL-ewXUuL-bXPx77-8CEaDU-cha7yy-965NsL-7z7a5z-bXMMao-eY8dHe-jF3yxs-9GzRxj-9qPXJ4-9Jz6Q4-ekEBbH-bmvbff-bXN29U-8Vx5ze-8VA7Ab-8Vx5ba-9Jzkxp-qwVfMm-bXMr8S-bXMqHC-6EqX5C-bXMqeY-bXMJ3G-bXMr6b-9ZRAEU-8PjmEZ-9GzUb5-fK1pzx-artr2f-chawmU-cEEZW7-ciLpz7-fxRofL\">Contractors Review Plans</a>\" by <a href=\"https://www.flickr.com/photos/usacehq/\">U.S. Army Corps of Engineers</a> is licensed under <a href=\"https://creativecommons.org/licenses/by/2.0/\">CC BY 2.0</a>', 'Events', '', 'inherit', 'closed', 'closed', '', '838-revision-v1', '', '', '2016-03-31 13:19:02', '2016-03-31 13:19:02', '', '838', 'http://www.texaspaceauthority.org/838-revision-v1/', '0', 'revision', '', '0');
